(function(){"use strict";var cookieName='quaddictedCookieConsent';var cookieLifetime=365;var _setCookie=function(cname,cvalue,exdays){var d=new Date();d.setTime(d.getTime()+(exdays*24*60*60*1000));var expires="expires="+d.toUTCString();document.cookie=cname+"="+cvalue+";"+expires+";path=/"};var _getCookie=function(cname){var name=cname+"=";var ca=document.cookie.split(';');for(var i=0;i<ca.length;i+=1){var c=ca[i];while(c.charAt(0)==' '){c=c.substring(1)}if(c.indexOf(name)==0){return c.substring(name.length,c.length)}}return ""};var _shouldShowPopup=function(){if(_getCookie(cookieName)){return false}else{return true}};if(_shouldShowPopup()){$('#cookieModal').modal('show')}$('#cookieModalConsent').on('click',function(){_setCookie(cookieName,1,cookieLifetime)});$('#cookieModalDecline').on('click',function(){_setCookie(cookieName,0,cookieLifetime)})})();